import React from 'react';
import { useState, useEffect } from 'react';
import styled from "styled-components";

import ic_mike_on from '../img/ic_mike_on_180.png';
import ic_mike_off from '../img/ic_mike_off_180.png'

const Button =styled.img`
  border-radius: 60px;
  margin-top:30px;
  cursor:pointer;
`

const MikeButton = () => {
  const [btn, setBtn] = useState(true);
  return (
    <>
      <Button
        onClick={()=>{setBtn(!btn)}}
        src={btn ? ic_mike_on :ic_mike_off} />
    </>
  );
}

export default MikeButton;
