import React from 'react';
import { useState, useEffect } from 'react';
import styled from "styled-components";

import ic_mike_on from '../img/ic_mic_on_180.png';
import ic_mike_off from '../img/ic_mic_off_180.png'

const Button =styled.img`
  border-radius: 60px;
  margin-top:30px;
  cursor:pointer;
`

const MicButton = (props) => {
const { listening } = props
  return (
    <>
      <Button
        src={listening ? ic_mike_on :ic_mike_off} />
    </>
  );
}

export default MicButton;
